mapper.select:
SELECT
a.id, a.code, a.name, a.acl_module_id, a.url, a.type,
a.status,a.seq,a.remark,
b.name AS moduleName
FROM sys_acl a
LEFT JOIN
sys_acl_module b ON
a.acl_module_id = b.id


		and a.name like #{name}


		and b.id = #{moduleId}


		or b.level like #{moduleLevel}


LIMIT #{start}, #{rows}

SELECT
count(1)
FROM sys_acl a
LEFT JOIN sys_acl_module b
ON
a.acl_module_id = b.id


		and a.name like #{name}


		and b.id = #{moduleId}


		or b.level like #{moduleLevel}


LIMIT #{start}, #{rows}

